mapper.select:
select * from article
where title like #{keyword} or content like #{keyword}
order by wroteAt desc limit 10 offset #{offset}

select * from article
where likeCnt > 0 and wroteAt >= #{date}
order by likeCnt desc limit 5

mapper.update:
update article
set topic=#{topic}, title=#{title}, content=#{content}
where no=#{no}
