mapper.select:
SELECT no, band_no, member_id, role, approved, joined_at
FROM band_member
WHERE band_no = #{band_no}
ORDER BY joined_at DESC;

SELECT no, band_no, member_id, role, approved, joined_at
FROM band_member
WHERE id = #{bandId};

mapper.update:
UPDATE band_member
SET approved = #{approved}
WHERE no = #{bandNo};
